<template>
    <div class="comp-navbar">
        <!-- 占位栏 -->
        <view class="placeholder-bar" :style="{height: navBarHeight + 'px'}"> </view>
        <!-- 导航栏主体 -->
        <view class="navbar" :style="{height: navBarHeight + 'px'}">
            <!-- 状态栏 -->
            <view class="nav-statusbar" :style="{height: statusBarHeight + 'px'}"></view>
            <!-- 标题栏 -->
            <view class="nav-titlebar" :style="{height: titleBarHeight + 'px' }">
                <!-- home及后退键 -->
                <view class="bar-options">
                    <view v-if="backVisible" class="opt opt-back" @click="backClick()">
                    <image class="back-image" src="/static/images/fanhui@2x.png"></image>
                    </view>
                    <view class="line" v-if="backVisible && homePath"></view>
                    <view v-if="homePath" class="opt opt-home" @click="homeClick()">
                    <image class="home-image" src="/static/images/home.png"></image>
                    </view>
                </view>
                <!-- 标题 -->
                <view class="bar-title" :style="{color:titleColor}">{{title}}</view>
            </view>
        </view>
    </div>
</template>

<script>
import wx from 'wx';
export default {
    props: {
        // 导航栏背景色
        navBackgroundColor: {
            default: "#ffffff"
            // default: "linear-gradient(0deg,rgba(38,165,220,1),rgba(62,72,246,1));opacity:0.83;"
        },
        // 标题颜色
        titleColor: {
            default: "red"
        },
        //标题文字
        title: {
            required: false,
            default: ""
        },
        // 是否显示后退按钮
        backVisible: {
            required: false,
            default: false
        },
        // home按钮的路径
        homePath: {
            required: false,
            default: ""
        }
    },
    data() {
        return {
            statusBarHeight: "", // 状态栏高度
            titleBarHeight: "", // 标题栏高度
            navBarHeight: "", // 导航栏总高度
            platform: "",
            model: "",
            brand: "",
            system: ""
        };
    },
    beforeMount() {
        const self = this;
        wx.getSystemInfo({
            success(system) {
                console.log(`system:`, system);
                self.statusBarHeight = system.statusBarHeight;
                self.platform = system.platform;
                self.model = system.model;
                self.brand = system.brand;
                self.system = system.system;
                let platformReg = /ios/i;
                if (platformReg.test(system.platform)) {
                    self.titleBarHeight = 44;
                } else {
                    self.titleBarHeight = 48;
                }
                self.navBarHeight = self.statusBarHeight + self.titleBarHeight;
            }
        });
    },
    mounted() {
        console.log(`this.backVisible:`, this.backVisible);
    },
    methods: {
        backClick() {
            if (getCurrentPages().length == 1) {
                // 打开分享卡片无法回退
                wx.redirectTo({
                    url: this.homePath
                });
            } else {
                wx.navigateBack({
                    delta: 1
                });
            }
        },
        homeClick() {
            wx.redirectTo({
                url: this.homePath
            });
        }
    }
};
</script>

